import os
from datetime import datetime
from flask import Flask, render_template_string, request, redirect, url_for, jsonify, flash
from flask_sqlalchemy import SQLAlchemy
from sqlalchemy.sql import func
import requests

APP_DIR = os.path.dirname(os.path.abspath(__file__))
DB_PATH = os.path.join(APP_DIR, 'farmers.db')
WEATHER_API_KEY = os.environ.get('OPENWEATHER_API_KEY')

app = Flask(__name__)
app.config['SQLALCHEMY_DATABASE_URI'] = f'sqlite:///{DB_PATH}'
app.config['SQLALCHEMY_TRACK_MODIFICATIONS'] = False
app.config['SECRET_KEY'] = os.environ.get('SECRET_KEY', 'devkey')

db = SQLAlchemy(app)

# ---------- Models ----------
class State(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    name = db.Column(db.String, unique=True, nullable=False)
    lgas = db.relationship('LGA', backref='state', lazy=True)

class LGA(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    name = db.Column(db.String, nullable=False)
    state_id = db.Column(db.Integer, db.ForeignKey('state.id'), nullable=False)

class Farmer(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    name = db.Column(db.String, nullable=False)
    phone = db.Column(db.String)
    state_id = db.Column(db.Integer, db.ForeignKey('state.id'))
    lga_id = db.Column(db.Integer, db.ForeignKey('l_g_a.id'))
    farm_location = db.Column(db.String)
    created_at = db.Column(db.DateTime(timezone=True), server_default=func.now())
    records = db.relationship('PlantingRecord', backref='farmer', lazy=True)

class PlantingRecord(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    farmer_id = db.Column(db.Integer, db.ForeignKey('farmer.id'), nullable=False)
    year = db.Column(db.Integer, nullable=False)
    season = db.Column(db.String, nullable=True)
    crop = db.Column(db.String, nullable=False)
    area_ha = db.Column(db.Float, default=0.0)
    yield_estimate = db.Column(db.Float, default=0.0)
    poultry = db.Column(db.Boolean, default=False)
    notes = db.Column(db.Text)
    recorded_at = db.Column(db.DateTime(timezone=True), server_default=func.now())

